function [selectPoint,selectPath,MinTime]=SelectTwo(position,G,FivePoint)%FivePoint=[93,102,104,110,117]index=1;p1={};p2={};TenPath={};TenTime=zeros(1,10); %存储对应路径的时间for i=1:5    for j=i+1:5        p1{index}=[FivePoint(i),FivePoint(j)];        p2{index}=[(3:8),FivePoint(i),FivePoint(j)];        index=index+1;    endendfor i=1:10    [tuplePath1,tuplePathNext,tuplePath,Time] = GetPathAndTime2(position,p1{i});    TenPath{i}=tuplePath;endfor i=1:10    TenTime(i)=TrueExposetime(TenPath{i},G,position,p2{i});endMinTime=min(TenTime);for i=1:10    if TenTime(i)==MinTime        index=i;        break;    endendselectPoint=p1{i};selectPath=TenPath{i};end